use yew::prelude::*;

#[function_component(Changelog)]
pub fn changelog() -> Html {
    // Scroll to top only on initial mount
    {
        use_effect_with_deps(
            move |_| {
                if let Some(window) = web_sys::window() {
                    window.scroll_to_with_x_and_y(0.0, 0.0);
                }
                || ()
            },
            (),
        );
    }
    html! {
        <div class="changelog-list-page">
            <div class="changelog-list-background"></div>
            <section class="changelog-list-hero">
                <h1>{"Changelog"}</h1>
                <p>{"Latest updates and changes for Lightfriend"}</p>
            </section>
            <section class="changelog-list-section">
                <div class="changelog-entry">
                    <div class="changelog-entry">
                                                <blockquote class="twitter-tweet"><p lang="en" dir="ltr">{"today i made lightfriend also ignore messages coming from chat rooms that have been muted in app already. \nAlso improved digest generation format to be more easy to read and grouped messages from same chat network to same line in the sms message. \nAlso added toggle for critical… "}<a href="https://t.co/oLC9WLODky">{"pic.twitter.com/oLC9WLODky"}</a></p>{"&mdash; rasmus (@ahtavarasm_us) "}<a href="https://twitter.com/ahtavarasm_us/status/1962589533448306725?ref_src=twsrc%5Etfw">{"September 1, 2025"}</a></blockquote> <script src="https://platform.twitter.com/widgets.js" charset="utf-8"></script>
                </div>

                </div>

                <div class="changelog-entry">
                <blockquote class="twitter-tweet"><p lang="en" dir="ltr">{"forgot to mention that now with lightfriend you can also send new emails and reply to existing emails using sms and voice calls "}<a href="https://t.co/XJ1OIS1TZG">{"https://t.co/XJ1OIS1TZG"}</a></p>{"&mdash; rasmus (@ahtavarasm_us) "}<a href="https://twitter.com/ahtavarasm_us/status/1959690290593230945?ref_src=twsrc%5Etfw">{"August 24, 2025"}</a></blockquote> <script src="https://platform.twitter.com/widgets.js" charset="utf-8"></script> 
                </div>
                <div class="changelog-entry">
                    <blockquote class="twitter-tweet"><p lang="en" dir="ltr">{"today i changed how sending emails and chat messages works on lightfriend. instead of having to confirm with a back and forth message each time when sending something, now it just adds the leaving message into a queue for 60seconds and if user is okay with the message and don't…"}</p>{"&mdash; rasmus (@ahtavarasm_us) "}<a href="https://twitter.com/ahtavarasm_us/status/1959684038676934768?ref_src=twsrc%5Etfw">{"August 24, 2025"}</a></blockquote> <script src="https://platform.twitter.com/widgets.js" charset="utf-8"></script>

                </div>
                <div class="changelog-entry">
                    <blockquote class="twitter-tweet"><p lang="en" dir="ltr">{"Signal messenger now also works on lightfriend along with whatsapp and telegram! i fixed the bug with message sending with confirmation setting on so you can now send messages to those platforms using voice calls and text messages. "}<a href="https://t.co/NOUNLaHBmN">{"pic.twitter.com/NOUNLaHBmN"}</a></p>{"&mdash; rasmus (@ahtavarasm_us) "}<a href="https://twitter.com/ahtavarasm_us/status/1957743131882893332?ref_src=twsrc%5Etfw">{"August 19, 2025"}</a></blockquote> <script src="https://platform.twitter.com/widgets.js" charset="utf-8"></script>
                </div>
                <div class="changelog-entry">
                    <blockquote class="twitter-tweet"><p lang="en" dir="ltr">{"today i made a change log page with all these X posts embedded. Also made prompts more reliable for setting waiting checks both on sms and voice calls. Also configured the signal bridge so it's now running on my mac locally - maybe live tomorrow if everything goes well. "}<a href="https://t.co/nCPI4w5LuB">{"pic.twitter.com/nCPI4w5LuB"}</a></p>{"&mdash; rasmus (@ahtavarasm_us) "}<a href="https://twitter.com/ahtavarasm_us/status/1956810473753788745?ref_src=twsrc%5Etfw">{"August 16, 2025"}</a></blockquote> <script src="https://platform.twitter.com/widgets.js" charset="utf-8"></script>
                </div>

                <div class="changelog-entry">

                <blockquote class="twitter-tweet"><p lang="en" dir="ltr">{"today i fixed bugs on lightfriends priority sender and waiting check functions and they should be now better at matching content. also added ability to set call or sms to each individual priority sender and waiting check so you can set them on the dashboard or by telling… "}<a href="https://t.co/R6umnBBKHz">{"pic.twitter.com/R6umnBBKHz"}</a></p>{"&mdash; rasmus (@ahtavarasm_us) "}<a href="https://twitter.com/ahtavarasm_us/status/1956449164663161005?ref_src=twsrc%5Etfw">{"August 15, 2025"}</a></blockquote> <script src="https://platform.twitter.com/widgets.js" charset="utf-8"></script>
                </div>
                <div class="changelog-entry">
                <blockquote class="twitter-tweet"><p lang="en" dir="ltr">{"today i added directions tool to lightfriend so you can navigate using step by step directions or get time estimates for different transit modes. I also started implementing uber tool, but it requires some scopes that have to be approved from uber so it&#39;s gonna take a while. "}<a href="https://t.co/ZNwDbkff44">{"pic.twitter.com/ZNwDbkff44"}</a></p>{"&mdash; rasmus (@ahtavarasm_us) "}<a href="https://twitter.com/ahtavarasm_us/status/1954242357781786822?ref_src=twsrc%5Etfw">{"August 9, 2025"}</a></blockquote> <script src="https://platform.twitter.com/widgets.js" charset="utf-8"></script>
                </div>
                <div class="changelog-entry">
                <blockquote class="twitter-tweet"><p lang="en" dir="ltr">{"the estimates are more for the global users who have to pay for their own messages. for the US users it doesn&#39;t really matter since they have enough messages included in the plan anyways. "}<a href="https://t.co/3ug2KRFOze">{"pic.twitter.com/3ug2KRFOze"}</a></p>{"&mdash; rasmus (@ahtavarasm_us) "}<a href="https://twitter.com/ahtavarasm_us/status/1953931596777640045?ref_src=twsrc%5Etfw">{"August 8, 2025"}</a></blockquote> <script src="https://platform.twitter.com/widgets.js" charset="utf-8"></script>
                </div>
                <div class="changelog-entry">
                <blockquote class="twitter-tweet"><p lang="en" dir="ltr">{"today i gave lightfriend a list of nearby places to you so its stt works better when you ask for the unknown nearby place weather or store information. previously it struggled to transcribe those and no wonder with some of these finnish place names like &#39;Sääksjärvi&#39; xd "}<a href="https://t.co/V4mVO6SUQm">{"pic.twitter.com/V4mVO6SUQm"}</a></p>{"&mdash; rasmus (@ahtavarasm_us) "}<a href="https://twitter.com/ahtavarasm_us/status/1952804986905051559?ref_src=twsrc%5Etfw">{"August 5, 2025"}</a></blockquote> <script src="https://platform.twitter.com/widgets.js" charset="utf-8"></script>
                </div>
                <div class="changelog-entry">
                <blockquote class="twitter-tweet"><p lang="en" dir="ltr">{"today i got telegram working mostly! i also decided that focusing on now the the self hosted version is a waste of time which i could be spending on making the service just as good as possible. i was spreading myself too thin. i already have customers so i should just focus on… "}<a href="https://t.co/FbkhtbARjG">{"pic.twitter.com/FbkhtbARjG"}</a></p>{"&mdash; rasmus (@ahtavarasm_us) "}<a href="https://twitter.com/ahtavarasm_us/status/1952046649296978260?ref_src=twsrc%5Etfw">{"August 3, 2025"}</a></blockquote> <script src="https://platform.twitter.com/widgets.js" charset="utf-8"></script>
                </div>
                <div class="changelog-entry">
                <blockquote class="twitter-tweet"><p lang="en" dir="ltr">{"today i researched if how i would implement zero access private llm inference to lightfriend with tinfoil dot sh and how i would package the self hosting option into a nice setup flow. then i implemented tinfoil nicely to the app and tested it and the models they offered didn&#39;t… "}</p>{"&mdash; rasmus (@ahtavarasm_us) "}<a href="https://twitter.com/ahtavarasm_us/status/1951704832500175109?ref_src=twsrc%5Etfw">{"August 2, 2025"}</a></blockquote> <script src="https://platform.twitter.com/widgets.js" charset="utf-8"></script>
                </div>
                <div class="changelog-entry">
                <blockquote class="twitter-tweet"><p lang="en" dir="ltr">{"this morning:<br>&gt;wake up, check dumbphone<br>&gt;lightfriend has sent me morning digest message with emails+whatsapps i should now about<br>&gt;one client program was having a bug<br>&gt;i fixed it straight from bed and sent back an email saying it&#39;s fixed<br>&gt;i went canoeing with my dumbphone like… "}<a href="https://t.co/d3HNkUDu0f">{"pic.twitter.com/d3HNkUDu0f"}</a></p>{"&mdash; rasmus (@ahtavarasm_us) "}<a href="https://twitter.com/ahtavarasm_us/status/1951353607531429977?ref_src=twsrc%5Etfw">{"August 1, 2025"}</a></blockquote> <script src="https://platform.twitter.com/widgets.js" charset="utf-8"></script>
                </div>
                <div class="changelog-entry">
                <blockquote class="twitter-tweet"><p lang="en" dir="ltr">{"today i was avoiding all the harder things and fixed many little things instead in lightfriend. i tried implementing the alex hormozi&#39;s value equation to my landing page and telling my story there. i also made the yesterdays country calculator to pick up your country based on ip… "}<a href="https://t.co/yRKiptp5Be">{"pic.twitter.com/yRKiptp5Be"}</a></p>{"&mdash; rasmus (@ahtavarasm_us) "}<a href="https://twitter.com/ahtavarasm_us/status/1951013649562157455?ref_src=twsrc%5Etfw">{"July 31, 2025"}</a></blockquote> <script src="https://platform.twitter.com/widgets.js" charset="utf-8"></script>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                
                </div>
            </section>
            <style>
                {r#"
                .changelog-list-page {
                    padding-top: 74px;
                    min-height: 100vh;
                    color: #ffffff;
                    position: relative;
                    background: transparent;
                }
                .changelog-list-background {
                    position: fixed;
                    top: 0;
                    left: 0;
                    width: 100%;
                    height: 100vh;
                    background-image: url('/assets/field_asthetic.webp');
                    background-size: cover;
                    background-position: center;
                    background-repeat: no-repeat;
                    opacity: 1;
                    z-index: -2;
                    pointer-events: none;
                }
                .changelog-list-background::after {
                    content: '';
                    position: absolute;
                    bottom: 0;
                    left: 0;
                    width: 100%;
                    height: 50%;
                    background: linear-gradient(
                        to bottom,
                        rgba(26, 26, 26, 0) 0%,
                        rgba(26, 26, 26, 1) 100%
                    );
                }
                .changelog-list-hero {
                    text-align: center;
                    padding: 6rem 2rem;
                    background: rgba(26, 26, 26, 0.75);
                    backdrop-filter: blur(5px);
                    margin-top: 2rem;
                    border: 1px solid rgba(30, 144, 255, 0.1);
                    margin-bottom: 2rem;
                }
                .changelog-list-hero h1 {
                    font-size: 3.5rem;
                    margin-bottom: 1.5rem;
                    background: linear-gradient(45deg, #fff, #7EB2FF);
                    -webkit-background-clip: text;
                    -webkit-text-fill-color: transparent;
                }
                .changelog-list-hero p {
                    font-size: 1.2rem;
                    color: #999;
                    max-width: 600px;
                    margin: 0 auto;
                }
                .changelog-list-section {
                    max-width: 800px;
                    margin: 0 auto;
                    padding: 2rem;
                }
                .changelog-entry {
                    background: rgba(26, 26, 26, 0.85);
                    backdrop-filter: blur(10px);
                    border: 1px solid rgba(30, 144, 255, 0.1);
                    border-radius: 12px;
                    margin-bottom: 2rem;
                    overflow: hidden;
                    transition: all 0.3s ease;
                    padding: 1rem;
                }
                .changelog-entry:hover {
                    border-color: rgba(30, 144, 255, 0.3);
                    transform: translateY(-5px);
                }
                .twitter-tweet {
                    margin: 0 auto;
                }
                @media (max-width: 768px) {
                    .changelog-list-hero {
                        padding: 4rem 1rem;
                    }
                    .changelog-list-hero h1 {
                        font-size: 2.5rem;
                    }
                    .changelog-list-section {
                        padding: 1rem;
                    }
                }
                "#}
            </style>
        </div>
    }
}
